import './index.scss';
import Navbar from '../Navbar';
import { io } from 'socket.io-client';
import { useState, useEffect } from "react";

const socket = io();
socket.on('alert', (msg) => {
    alert(msg);
});

const Homepage = () => {
    const init = (
        <div className={ 'game-options' }>
            <a onClick={
                () => {
                    useEffect(() => {
                        const page = (
                            <>
                                create
                            </>
                        );

                        setMain(page);
                    })
                }
            }>+ Create Game</a>

            <a onClick={
                () => {
                    useEffect(() => {
                        const page = (
                            <>
                                join
                            </>
                        );

                        return setMain(page);
                    })
                }
            }>+ Join Game</a>
        </div>
    );

    const [main, setMain] = useState(init);

    return (
        <div className={ 'home' }>
            <Navbar />

            { main }
        </div>
    );
}

export default Homepage;
