import './index.scss';
import Navbar from '../Navbar';
import { io } from 'socket.io-client';
import { useState, useEffect } from "react";
import {NavLink} from "react-router-dom";

const socket = io();
socket.on('alert', (msg) => {
    alert(msg);
});

const Create = () => {
    const init = (
        <div>
            Loading . . .
        </div>
    );
    const [packs, setPacks] = useState(init);

    useEffect(() => {
        (async () => {
            //get cards https://www.restagainsthumanity.com/api/v2/cards?packs=
            //get packs https://www.restagainsthumanity.com/api/v2/packs

            const getPacks = await fetch('https://www.restagainsthumanity.com/api/v2/packs', {
                method: 'GET'
            }).then((res) => res.json());

            setPacks(
                (
                    <>
                        <p id={ 'pre-title' }>Premade Packs</p>
                        <div id={ 'base-packs' }>
                            { getPacks.map((p) => (
                                    <label className={ 'pack-container' }>
                                        <input type={ 'checkbox' } />
                                        { p }
                                    </label>
                                )
                            )}
                        </div>
                    </>
                )
            );
        })();
    })

    return (
        <>
            <Navbar />

            <div className={ 'main' }>

                <form onSubmit={ (e) => e.preventDefault() } id={ 'game-create' }>
                    <div className={ 'row-wrapper' }>
                        { packs }
                        <h1> OR </h1>

                    </div>
                </form>
            </div>
        </>
    );
}

export default Create;
