import './index.scss';
import Navbar from '../Navbar';
import { io } from 'socket.io-client';
import { useState, useEffect } from "react";

const socket = io();
socket.on('alert', (msg) => {
    alert(msg);
});

const Homepage = () => {
    const init = (
        <div className={ 'game-options' }>
            <a onClick={ create() }>+ Create Game</a>
            <a onClick={ join() }>+ Join Game</a>
        </div>
    );

    const [main, setMain] = useState(init);

    const join = () => useEffect(() => {
        const page = (
            <>
                join
            </>
        );

        setMain(page);
    });

    const create = () => useEffect(() => {
        const page = (
            <>
                create
            </>
        );

        setMain(page);
    });

    return (
        <div className={ 'home' }>
            <Navbar />

            { main }
        </div>
    );
}

export default Homepage;
