import './index.scss';
import Navbar from '../Navbar';
import { io } from 'socket.io-client';
import { useState, useEffect } from "react";
import {NavLink} from "react-router-dom";

const socket = io();
socket.on('alert', (msg) => {
    alert(msg);
});

const Create = () => {
    const init = (
        <div>
            Loading . . .
        </div>
    );
    const [packs, setPacks] = useState(init);

    useEffect(() => {
        (async () => {
            //get cards https://www.restagainsthumanity.com/api/v2/cards?packs=
            //get packs https://www.restagainsthumanity.com/api/v2/packs

            const getPacks = await fetch('https://www.restagainsthumanity.com/api/v2/packs', {
                method: 'GET'
            }).then((res) => res.json());

            setPacks(
                (
                    <form onSubmit={ (e) => e.preventDefault() } id={ 'base-packs' }>
                        { getPacks.map((p) => (
                            <label className={ 'pack-container' }>
                                <input type={ 'checkbox' } />
                                { p }
                            </label>)
                        )}
                        <input type={ 'submit' } value={ 'Start' } />
                    </form>
                )
            );
        })();
    })

    return (
        <>
            <Navbar />

            <div className={ 'main' }>

                { packs }
            </div>
        </>
    );
}

export default Create;
