import './index.scss';
import Navbar from '../Navbar';
import { io } from 'socket.io-client';
import { useState, useEffect } from "react";
import {NavLink} from "react-router-dom";

const socket = io();
socket.on('alert', (msg) => {
    alert(msg);
});

const Create = () => {
    document.addEventListener('keyup', (e) => {
        e.preventDefault();
        if (e.key === 'ENTER') crHandler(e);
    });

    const init = (
        <div>
            Loading . . .
        </div>
    );

    const crInit = (
        <>
            <a className={ 'cr-add' }>
                <input type={ 'text' } placeholder={'This website supports custom decks made with CR Cast'} id={ 'cr-cast-input' } />
                <input type={'submit'} onClick={ (e) => crHandler(e) } value={ 'Add CR Cast Deck' } id={ 'cr-cast-submit' } />
            </a>
        </>
    );

    const [packs, setPacks] = useState(init);
    let [crPacks, setCR] = useState(crInit);

    async function crHandler(e) {
        e.preventDefault();
        // https://api.crcast.cc/v1/cc/decks/
        const pack = await fetch('/api/cr-cast', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                deck: document.getElementById('cr-cast-input').value
            })
        }).then((res) => res.json());

        if(pack.error > 1) return alert('Invalid pack, please check that you entered the right code.');

        const packID = ( <label className={ 'pack-container' }><input type={ 'checkbox' }/> { pack.name }: { pack.description } </label>);
        const add = (
            <>
                { crPacks }
                { packID }
            </>
        );

        crPacks = add;

        setCR(add);
    }

    useEffect(() => {
        (async () => {
            //get cards https://www.restagainsthumanity.com/api/v2/cards?packs=
            //get packs https://www.restagainsthumanity.com/api/v2/packs

            const getPacks = await fetch('https://www.restagainsthumanity.com/api/v2/packs', {
                method: 'GET'
            }).then((res) => res.json());

            setPacks(
                (
                    <div className={ 'column-wrapper' }>
                        <p id={ 'pre-title' }>Premade Packs</p>
                        <div id={ 'base-packs' }>
                            { getPacks.map((p) => (
                                    <label className={ 'pack-container' }>
                                        <input type={ 'checkbox' } />
                                        { p }
                                    </label>
                                )
                            )}
                        </div>
                    </div>
                )
            );
        })();
    });

    return (
        <>
            <Navbar />

            <div className={ 'main' }>

                <form id={ 'game-create' }>
                    <div className={ 'row-wrapper' }>
                        { packs }
                        <div className={ 'column-wrapper' } id={ 'cr-cast-div' }>
                            <h1> CR Cast </h1>
                            { crPacks }
                        </div>
                    </div>

                    <div id={ 'create-game-top' }>
                        <input type={'text'} placeholder={ 'Password, if left blank game will be public' } />
                        <input type={ 'submit' } value={ 'Start Game!' } />
                    </div>
                </form>
            </div>
        </>
    );
}

export default Create;
