import './index.scss';
import Navbar from '../Navbar';
import { io } from 'socket.io-client';
import { useState, useEffect } from "react";
import {NavLink} from "react-router-dom";

const socket = io();
socket.on('alert', (msg) => {
    alert(msg);
});

socket.on('redirect', (link) => {
    window.location.href = link;
});

const text = document.querySelector('.circle');

text.innerHTML = text.innerHTML
    .split('')
    .map((chars, i) => (<span style={ 'transform: rotate('+ i * 10.3 +'deg)' }> { char } </span>))

const Homepage = () => {
    const init = (
        <div className={ 'circle' }>Loading . . . </div>
    )

    const page = (
        <div className={ 'home' }>
            <div className={ 'game-options' }>
                <title>Card Me Daddy</title>
                <NavLink to={ '/create-game' }>+ Create Game</NavLink>

                <NavLink to={ '/join-game' }>+ Join Game</NavLink>
            </div>
        </div>
    )

    const [main, setMain] = useState(init);

    return (
        <>
            <Navbar />

            { main }
        </>
    );
}

export default Homepage;
